import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { every } from "./API/contact";

const Edit=()=>{
    const[expert,setExpert]=useState({
        fullname:"",
        contact:0,
        commercials:0,
        experience:0,
        expert:""
    })
    const{id}=useParams()

    useEffect(()=>{
        loadExpert()
    })

    const loadExpert=async(id)=>{
        const exp=await every(id)
        setExpert(exp.data)
    }

    return(
        <>
            
        </>
    );
}
export default Edit;
