  useEffect(()=>{
    getGreet()
  },[])


  const getGreet=async()=>{
    const sort=await greetings()
    alert(sort.data+" in use effect")
    setMessage(sort.data)
  }

  return (
    <>
      <h1>{message}</h1>
    </>
    );
}

